import { Injectable } from '@angular/core';
import { HttpClient } from "@angular/common/http";
import { Album } from '../_models/album-model';
import { AlbumPhoto } from '../_models/album-photo-model';
import { of } from 'rxjs';
import { tap } from 'rxjs/operators';


@Injectable({
  providedIn: 'root'
})
export class AlbumsService {
  BASE_URL = 'https://jsonplaceholder.typicode.com';

  constructor(private httpClient: HttpClient) { }

  getAlbums() {
    const localAlbums = localStorage.getItem('albums');
    if (localAlbums) {
      return of(JSON.parse(localAlbums));
    } else {
      return this.httpClient.get<Album[]>(`${this.BASE_URL}/albums`);
    }
  }

  getAlbum(id: number) {
    return this.httpClient.get<Album>(`${this.BASE_URL}/albums/${id}`);
  }

  addAlbum(album: Album) {
    // Эмулируем добавление альбома на "сервер", который в данном случае — это localStorage.
    return of(album).pipe(
        tap(newAlbum => {
          // Получаем список альбомов из localStorage, или, если его нет, то пустой массив.
          const localAlbums = JSON.parse(localStorage.getItem('albums') || '[]');
          // Эмулируем уникальный ID, добавляя текущее время. В реальном приложении ID должен генерировать сервер.
          newAlbum.id = new Date().getTime();
          // Добавляем новый альбом в начало массива.
          localAlbums.unshift(newAlbum);
          // Сохраняем обновленный массив альбомов обратно в localStorage.
          localStorage.setItem('albums', JSON.stringify(localAlbums));
          // Обновляем состояние локального компонента.
          this.albums.unshift(newAlbum);
        })
    );
  }


  deleteAlbum(id: number) {
    return this.httpClient.delete<Album>(`${this.BASE_URL}/albums/${id}`);
  }

  updateAlbum(albumId: number, newTitle: string) {
    const body = { title: newTitle };
    console.log(newTitle);
    return this.httpClient.put<Album>(`${this.BASE_URL}/albums/${albumId}`, body);
  }

  getAlbumPhotos(id: number) {
    return this.httpClient.get<AlbumPhoto[]>(`${this.BASE_URL}/albums/${id}/photos`);
  }
}
